package com.devopworld.weatherapp.feature.mvvm.viewimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.TextViewimport androidx.recyclerview.widget.RecyclerViewimport com.devopworld.weatherapp.Rimport com.devopworld.weatherapp.feature.mvvm.model.DayWiseTempclass ForecastRecyclerAdapter(val map: HashMap<Int, DayWiseTemp>) :    RecyclerView.Adapter<ForecastViewHolder>() {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ForecastViewHolder {        val view = LayoutInflater.from(parent.context)            .inflate(R.layout.list_item_view, parent, false)        return ForecastViewHolder(view)    }    override fun getItemCount(): Int = map.size    override fun onBindViewHolder(holder: ForecastViewHolder, position: Int) {        holder.dayTextView.text = map[position]?.day ?: ""        val tempWithDegree = "${map[position]?.temp} C"        holder.temperatureTextView.text = tempWithDegree    }}class ForecastViewHolder(view: View) : RecyclerView.ViewHolder(view) {    val dayTextView = view.findViewById<TextView>(R.id.dayTextView)    val temperatureTextView = view.findViewById<TextView>(R.id.temperature)}